                sText = sText.Substring(0, (nPos - 1) * 3) + sText.Substring((nPos - 1) * 3 + 3, ((nCount - i + 1) - nPos) * 3);
            }

            return sText2;
        }

        private void fFLoad()
        {
            String sName = "Data3.txt";
            String sLine;
            int nPos;

            try
            {   // Open the text file using a stream reader.
                using (StreamReader sr = new StreamReader(sName))
                {

                    // Read the stream to a string, and write the string to the console.
                    sLine = sr.ReadLine();
                    sLine = sr.ReadLine();
                    mnCount = Convert.ToInt32(sLine);
                    nPos = 1;
                    do
                    {
                        sLine = sr.ReadLine();
                        _data1[nPos - 1] = null;
                        sLine = sr.ReadLine();
                        _data2[nPos - 1] = sLine;
                        sLine = sr.ReadLine();
                        _data[nPos - 1] = sLine;
                        nPos += 1;
                    } while (nPos<=mnCount);
                    sr.Close();
                }


            }
            catch (Exception e1)
            {
                MessageBox.Show("The file could not be read:", e1.Message);
            }

        }

